import React, { useState } from "react";
import { FaAngleDown, FaAngleUp } from "react-icons/fa";
import { Transition } from "@headlessui/react";
import blogService from "../services/blogService"
import PopUp from "./ConformationWindow"

const onDeleteBlog = async(id)=> {
    const response = await blogService.del(id)
    const blogs = await blogService.getAll();
    setBlogs(blogs)
    console.log(response)
}

const handleDelete = () => {
    setShowPopup(true)
}



const Blog = ({ blog,onDeleteBlog }) => {
    const [toggleDetails, setToggleDetails] = useState(false);
    const [showPopup,setShowPopup] = useState(false)

    const toggleVisibility = () => {
        setToggleDetails(!toggleDetails);
    };

    return (
        <div className="bg-white p-4 rounded-lg shadow-lg mb-4">
            <div className="flex justify-between items-center mb-4">
                <h2 className="text-lg font-bold">{blog.title}</h2>
                <button
                    onClick={toggleVisibility}
                    className="p-2 rounded-md hover:bg-gray-200 focus:outline-none transition duration-300 ease-in-out flex items-center"
                >
                    {toggleDetails ? (
                        <FaAngleUp className="text-gray-600 mr-1" />
                    ) : (
                        <FaAngleDown className="text-gray-600 mr-1" />
                    )}
                </button>
            </div>
            <Transition
                show={toggleDetails}
                enter="transition-opacity duration-300"
                enterFrom="opacity-0"
                enterTo="opacity-100"
                leave="transition-opacity duration-300"
                leaveFrom="opacity-100"
                leaveTo="opacity-0"
            >
                <div className="mb-4">
                    <p className="text-gray-700 font-bold">Author:</p>
                    <p className="text-gray-700">{blog.author}</p>
                </div>
                <div className="mb-4">
                    <p className="text-gray-700 font-bold">Url:</p>
                    <p className="text-gray-700">{blog.url}</p>
                </div>
                <div className="flex items-center mb-4">
                    <button className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
                        Like
                    </button>
                    <p className="text-gray-700 ml-4">{blog.likes} likes</p>
                </div>
                <div className="flex items-center">
                <button className="bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                onClick={() => onDeleteBlog(blog.id)}>
                        Delete
                    </button>
                </div>
            </Transition>
        </div>
    );
};

export default Blog;
