import { useState, useEffect } from "react";

import LoginForm from "./components/LoginForm";
import blogService from "./services/blogs";
import BlogListItem from "./components/BlogListItem";
import Notification from "./components/Notification";

const App = () => {
    const [blogs, setBlogs] = useState([]);
    const [user, setUser] = useState(null);
    const [error, setError] = useState("");
    useEffect(() => {
        blogService.getAll().then((blogs) => setBlogs(blogs));
    }, []);

    if (user === null) {
        return (
            <div className="flex justify-center items-center flex-col bg-gray-100 py-8">
                {error && <Notification form="error" message={error} />}
                <LoginForm setUser={setUser} setError={setError} />
            </div>
        );
    }
    return (
        <div className="flex justify-center items-center flex-col bg-gray-100 py-8">
            <h2 className="text-3xl font-bold text-center text-gray-800 bg-gradient-to-r from-purple-400 to-pink-400 bg-clip-text my-4">
                Blogs
            </h2>
            {blogs.map((blog) => (
                <BlogListItem
                    key={blog.id}
                    title={blog.title}
                    author={blog.author}
                />
            ))}
        </div>
    );
};

export default App;
