import axios from 'axios'
const baseUrl = '/api/blogs'
let token = null
const setToken = (s) => {
  token = "Bearer "+s
}
const getAll = async () => {
  const response = await axios.get(baseUrl)
  return response.data
}

// eslint-disable-next-line import/no-anonymous-default-export
export default { getAll }
