import { useState, useEffect } from 'react'

import LoginForm from "./components/LoginForm"
import blogService from './services/blogs'
import BlogListItem from './components/BlogListItem'
const App = () => {
  const [blogs, setBlogs] = useState([])
  const [user,setUser] = useState(null)

  useEffect(() => {
    blogService.getAll().then(blogs =>
      setBlogs( blogs )
    )  
  }, [])

   if (user === null)
   {
    return(
      <LoginForm/>
    )
   }
return (
    <div>
      <h2 className="text-3xl font-bold text-center text-gray-800 bg-gradient-to-r from-purple-400 to-pink-400 bg-clip-text mt-4">
                Blogs
            </h2>
      {blogs.map(blog =>
        <BlogListItem key={blog.id} title={blog.title} author={blog.author} />
      )}
    </div>
  )
}

export default App
