import axios from 'axios'
const baseUrl = '/api/blogs'
let token = null
const setToken = (s) => {
  token = "Bearer "+s
}
const getAll = () => {
  const request = axios.get(baseUrl)
  return request.then(response => response.data)
}

// eslint-disable-next-line import/no-anonymous-default-export
export default { getAll }
