import React, { useState } from "react";
import { Transition } from "@headlessui/react";
import { FaAngleDown, FaAngleUp } from "react-icons/fa";

const ToggleableComponent = ({ children, title }) => {
    const [isOpen, setIsOpen] = useState(true);

    const toggle = () => {
        setIsOpen(!isOpen);
    };

    return (
        <div className={`p-4 mb-3 ${isOpen ? "w-1/3" : ""}`}>
            <Transition
                show={isOpen}
                enter="transition-opacity duration-300"
                enterFrom="opacity-0"
                enterTo="opacity-100"
                leave="transition-opacity duration-300"
                leaveFrom="opacity-100"
                leaveTo="opacity-0"
            >
                <button
                    className={`flex items-center justify-between w-full py-2 text-lg font-bold text-purple-700 hover:text-purple-900 transition-colors duration-300 rounded-lg`}
                    onClick={toggle}
                >
                    <span>{title}</span>
                    <svg
                        xmlns="http://www.w3.org/2000/svg"
                        viewBox="0 0 20 20"
                        fill="currentColor"
                        className={`h-6 w-6 ${
                            isOpen ? "transform rotate-180" : ""
                        }`}
                    >
                        <path
                            fillRule="evenodd"
                            d="M10 17.758l-7.071-7.071a1 1 0 011.414-1.414L10 15.93l5.657-5.657a1 1 0 011.414 1.414L10 17.758z"
                            clipRule="evenodd"
                        />
                    </svg>
                </button>
                {isOpen && <div className="mt-4">{children}</div>}
            </Transition>
        </div>
    );
};

export default ToggleableComponent;
